				int count = 0;
				foreach (GrantSpending item in this)
				{
					this.OnDetailUpdate("GrantSpending",
						"Updating GrantSpending: " + (count + 1).ToString("###,###,###,##0") +
						" of " + this.Count.ToString("###,###,###,##0"),
						string.Empty,
						this.Count,
						count + 1);
					if (item.RecordStatus == RecordStatus.Modified ||
						item.RecordStatus == RecordStatus.New)
