		/// <summary>
		/// Converts the collection to a Dataset and passes it back.
		/// </summary>
		/// <param name="includeDeleted">False to remove all records marked for deletion before converting.</param>
		/// <returns>The collection converted to a DataSet object.</returns>
		public DataSet ToDataSet(bool includeDeleted)
		{
			// Create the DataSet you're going to send back
			DataSet ds = new DataSet();
			
			// Get the base datatable
			DataTable dt = this.ToDataTable(includeDeleted);
			ds.Tables.Add(dt);
			
			// Now get all the sub objects and link them in
			DataRow newRow = dt.NewRow();
			
			SessionElementCollection Coll_SessionElement = new SessionElementCollection();
			DataTable dtSessionElement = Coll_SessionElement.ToEmptyDataTable();
			
			SessionTabCollection Coll_SessionTab = new SessionTabCollection();
			DataTable dtSessionTab = Coll_SessionTab.ToEmptyDataTable();
			
			foreach (Session item in this)
			{
				// SessionElement
				dt = item.Coll_SessionElement.ToDataTable(includeDeleted);
				foreach (DataRow row in dt.Rows)
				{
					newRow = dtSessionElement.NewRow();
					foreach (DataColumn col in dtSessionElement.Columns)
					{
						 newRow[col.ColumnName] = row[col.ColumnName];
					}
					dtSessionElement.Rows.Add(newRow);
				}
				
				// SessionTab
				dt = item.Coll_SessionTab.ToDataTable(includeDeleted);
				foreach (DataRow row in dt.Rows)
				{
					newRow = dtSessionTab.NewRow();
					foreach (DataColumn col in dtSessionTab.Columns)
					{
						 newRow[col.ColumnName] = row[col.ColumnName];
					}
					dtSessionTab.Rows.Add(newRow);
				}
			}
			
			// Add the datatables to the dataset
			ds.Tables.Add(dtSessionElement);
			ds.Relations.Add("Session_SessionElement", ds.Tables[0].Columns["SessionGUID"], ds.Tables[ds.Tables.Count - 1].Columns["SessionGUID"]);
			
			ds.Tables.Add(dtSessionTab);
			ds.Relations.Add("Session_SessionTab", ds.Tables[0].Columns["SessionGUID"], ds.Tables[ds.Tables.Count - 1].Columns["SessionGUID"]);
			

			// Send back the dataset
			return ds;
		}
