			StringBuilder sbDeleted = new StringBuilder(LEAD_BaseObject.SplitOnXMLHeader);

			// Clear out the collections
			newElements = new List<string>();
			readElements = new List<string>();
			changedElements = new List<string>();
			deletedElements = new List<string>();

			// Go through the records and take care of them
			foreach (SessionElement item in this)
			{
				switch (item.RecordStatus)
				{
					case RecordStatus.Current:
						// Don't forget the to string
						sbRead.Append("<SessionElement>" + Environment.NewLine);
						sbRead.Append("\t<PropertyGUID>" + item.PropertyGUID + "</PropertyGUID>" + Environment.NewLine);
						sbRead.Append("\t<SessionGUID>" + item.SessionGUID + "</SessionGUID>" + Environment.NewLine);
						sbRead.Append("\t<PageKey>" + (item.PageKey != null ? item.PageKey : "NULL") + "</PageKey>" + Environment.NewLine);
						sbRead.Append("\t<ElementKey>" + (item.ElementKey != null ? item.ElementKey : "NULL") + "</ElementKey>" + Environment.NewLine);
						sbRead.Append("\t<ElementValueString>" + (item.ElementValueString != null ? item.ElementValueString : "NULL") + "</ElementValueString>" + Environment.NewLine);
						sbRead.Append("</SessionElement>" + Environment.NewLine);

						if (sbRead.ToString().Trim().Length > LEAD_BaseObject.SplitOnXMLCharactersForLogging) 
						{ 
							readElements.Add(sbRead.ToString());
							sbRead = new StringBuilder(LEAD_BaseObject.SplitOnXMLHeader); 
						}
						break;

					case RecordStatus.Deleted:
						sbDeleted.Append("<SessionElement>" + Environment.NewLine);
						sbDeleted.Append("\t<PropertyGUID>" + item.PropertyGUID + "</PropertyGUID>" + Environment.NewLine);
						sbDeleted.Append("\t<SessionGUID>" + item.SessionGUID + "</SessionGUID>" + Environment.NewLine);
						sbDeleted.Append("\t<PageKey>" + (item.PageKey != null ? item.PageKey : "NULL") + "</PageKey>" + Environment.NewLine);
						sbDeleted.Append("\t<ElementKey>" + (item.ElementKey != null ? item.ElementKey : "NULL") + "</ElementKey>" + Environment.NewLine);
						sbDeleted.Append("\t<ElementValueString>" + (item.ElementValueString != null ? item.ElementValueString : "NULL") + "</ElementValueString>" + Environment.NewLine);
						sbDeleted.Append("</SessionElement>" + Environment.NewLine);

						if (sbDeleted.ToString().Trim().Length > LEAD_BaseObject.SplitOnXMLCharactersForLogging)
						{
							deletedElements.Add(sbDeleted.ToString());
							sbDeleted = new StringBuilder(LEAD_BaseObject.SplitOnXMLHeader);
						}
						break;

					case RecordStatus.New:
						sbNew.Append("<SessionElement>" + Environment.NewLine);
						sbNew.Append("\t<PropertyGUID>" + item.PropertyGUID + "</PropertyGUID>" + Environment.NewLine);
						sbNew.Append("\t<SessionGUID>" + item.SessionGUID + "</SessionGUID>" + Environment.NewLine);
						sbNew.Append("\t<PageKey>" + (item.PageKey != null ? item.PageKey : "NULL") + "</PageKey>" + Environment.NewLine);
						sbNew.Append("\t<ElementKey>" + (item.ElementKey != null ? item.ElementKey : "NULL") + "</ElementKey>" + Environment.NewLine);
						sbNew.Append("\t<ElementValueString>" + (item.ElementValueString != null ? item.ElementValueString : "NULL") + "</ElementValueString>" + Environment.NewLine);
						sbNew.Append("</SessionElement>" + Environment.NewLine);

						if (sbNew.ToString().Trim().Length > LEAD_BaseObject.SplitOnXMLCharactersForLogging)
						{
							newElements.Add(sbNew.ToString());
							sbNew = new StringBuilder(LEAD_BaseObject.SplitOnXMLHeader);
						}
						break;

					case RecordStatus.Modified:
						sbChanged.Append(item.GetChangedElementsAsXML() + Environment.NewLine);

						if (sbChanged.ToString().Trim().Length > LEAD_BaseObject.SplitOnXMLCharactersForLogging)
						{
							changedElements.Add(sbChanged.ToString());
							sbChanged = new StringBuilder(LEAD_BaseObject.SplitOnXMLHeader);
						}
						break;

				}
			}

			// Add the final strings to the list
			newElements.Add(sbNew.ToString());
			readElements.Add(sbRead.ToString());
			deletedElements.Add(sbDeleted.ToString());
			changedElements.Add(sbChanged.ToString());
		}

		/// <summary>
		/// Load the XML into a collection based on what's passed in.
		/// </summary>
		/// <param name="xml">The XML to process.</param>
		/// <returns>The completed SessionElementCollection object.</returns>
		public static SessionElementCollection LoadFromXML(string xml)
		{
			SessionElementCollection coll = new SessionElementCollection();
			System.Xml.XmlDocument doc = new System.Xml.XmlDocument();
			doc.LoadXml(xml);
			// <SessionElementCollection>
			foreach (System.Xml.XmlNode nodeMain in doc.ChildNodes)
			{
				// <SessionElement>
				foreach (System.Xml.XmlNode node in nodeMain.ChildNodes)
				{
					// These are the elements <DetailGUID>, <UserName>, etc.
					SessionElement item = SessionElement.LoadFromXML(node.OuterXml);
					if (item != null) { coll.Add(item); }
				}
			}
			return coll;
		}
