		/// Load the XML into a collection based on what's passed in.
		/// </summary>
		/// <param name="xml">The XML to process.</param>
		/// <returns>The completed SessionTabCollection object.</returns>
		public static SessionTabCollection LoadFromXML(string xml)
		{
			SessionTabCollection coll = new SessionTabCollection();
			System.Xml.XmlDocument doc = new System.Xml.XmlDocument();
			doc.LoadXml(xml);
			// <SessionTabCollection>
			foreach (System.Xml.XmlNode nodeMain in doc.ChildNodes)
			{
				// <SessionTab>
				foreach (System.Xml.XmlNode node in nodeMain.ChildNodes)
				{
					// These are the elements <DetailGUID>, <UserName>, etc.
					SessionTab item = SessionTab.LoadFromXML(node.OuterXml);
					if (item != null) { coll.Add(item); }
				}
			}
			return coll;
		}
