		/// <returns>A copy of the object, with a new ID.</returns>
		public static User CopyFrom(User source)
		{
			// Make the copy of the object
			User item = source.Clone();
			
			// Change the ID field
			item.UserID = -1;

			// Reset the record status to new
			item.RecordStatus = RecordStatus.New;

			// Do the sub-collections
			#region Sub Collections
			for (int i = item.Coll_SecUserToGroup.Count - 1; i >= 0; i--)
			{
				item.Coll_SecUserToGroup[i] = (SecUserToGroup)item.Coll_SecUserToGroup[i].CopyWithNewID();
				item.Coll_SecUserToGroup[i].UserID = item.UserID;
			}
			for (int i = item.Coll_UserFavorite.Count - 1; i >= 0; i--)
			{
				item.Coll_UserFavorite[i] = (UserFavorite)item.Coll_UserFavorite[i].CopyWithNewID();
				item.Coll_UserFavorite[i].UserID = item.UserID;
			}
			for (int i = item.Coll_UserLoadedForm.Count - 1; i >= 0; i--)
			{
				item.Coll_UserLoadedForm[i] = (UserLoadedForm)item.Coll_UserLoadedForm[i].CopyWithNewID();
				item.Coll_UserLoadedForm[i].UserID = item.UserID;
