			foreach (User item in this)
			{
				// SecUserToGroup
				dt = item.Coll_SecUserToGroup.ToDataTable(includeDeleted);
				foreach (DataRow row in dt.Rows)
				{
					newRow = dtSecUserToGroup.NewRow();
