						 newRow[col.ColumnName] = row[col.ColumnName];
					}
					dtUserFavorite.Rows.Add(newRow);
				}
				
				// UserLoadedForm
				dt = item.Coll_UserLoadedForm.ToDataTable(includeDeleted);
				foreach (DataRow row in dt.Rows)
				{
					newRow = dtUserLoadedForm.NewRow();
					foreach (DataColumn col in dtUserLoadedForm.Columns)
					{
						 newRow[col.ColumnName] = row[col.ColumnName];
					}
					dtUserLoadedForm.Rows.Add(newRow);
				}
				
				// UserPref
				dt = item.Coll_UserPref.ToDataTable(includeDeleted);
				foreach (DataRow row in dt.Rows)
				{
					newRow = dtUserPref.NewRow();
					foreach (DataColumn col in dtUserPref.Columns)
					{
						 newRow[col.ColumnName] = row[col.ColumnName];
					}
					dtUserPref.Rows.Add(newRow);
				}
			}
			
			// Add the datatables to the dataset
			ds.Tables.Add(dtSecUserToGroup);
			ds.Relations.Add("User_SecUserToGroup", ds.Tables[0].Columns["UserID"], ds.Tables[ds.Tables.Count - 1].Columns["UserID"]);
			
			ds.Tables.Add(dtUserFavorite);
