			ds.Tables.Add(dtUserPref);
			ds.Relations.Add("User_UserPref", ds.Tables[0].Columns["UserID"], ds.Tables[ds.Tables.Count - 1].Columns["UserID"]);
			

			// Send back the dataset
			return ds;
		}
